const modalBtn=document.querySelectorAll("[data-modal]"),modalClose=document.querySelectorAll(".modal__close"),modal=document.querySelectorAll(".modal"),body=document.body;function closeModal(e){e.querySelector(".modal__inner").removeAttribute("style"),setTimeout(()=>{e.classList.remove("show"),body.classList.remove("no-scroll")},200)}modalBtn.forEach(e=>{e.addEventListener("click",e=>{e=e.currentTarget.getAttribute("data-modal"),e=document.getElementById(e);let o=e.querySelector(".modal__inner");o.addEventListener("click",e=>{e.stopPropagation()}),e.classList.add("show"),body.classList.add("no-scroll"),setTimeout(()=>{o.style.transform="none"},1)})}),modalClose.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget.closest(".modal"))})}),modal.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget)})});